        string select_sql = "SELECT id FROM directory_structure WHERE parent_id = ? AND level > ?";
        sqlite3_stmt* select_stmt;
        int result = sqlite3_prepare_v2(db_, select_sql.c_str(), select_sql.length() + 1, &select_stmt, nullptr);
        sqlite3_bind_int(select_stmt, 1, parent_id);
        sqlite3_bind_int(select_stmt, 2, level);
        if (result != SQLITE_OK) {
            throw runtime_error("Failed to prepare select statement");
        }
        while ((result = sqlite3_step(select_stmt)) == SQLITE_ROW) {
            int id = sqlite3_column_int(select_stmt, 0);
            delete_item(id);
        }
        if (result != SQLITE_DONE) {
            throw runtime_error("Failed to execute select statement");
        }
        sqlite3_finalize(select_stmt);
    }

    void recursively(string path) {
        int parent_id = -1;
        int level = 0;
        vector<string> paths = split(path, '/');
        for (const string& item : paths) {
            if (!item.empty()) {
                if (item.find('.') == string::npos) { // directory
                    parent_id = add_or_update_database(item, parent_id, level);
                    level++;
                } else { // file
                    add_file(item, parent_id, level, 10); // set file size to 10 for example
                }
            }
        }
    }

