        string sql = "DELETE FROM directory_structure WHERE id = ?";
        sqlite3_stmt* stmt;
        int result = sqlite3_prepare_v2(db_, sql.c_str(), sql.length() + 1, &stmt, nullptr);
        sqlite3_bind_int(stmt, 1, id);
