        }
        int id = -1;
        if ((result = sqlite3_step(select_stmt)) == SQLITE_ROW) {
            id = sqlite3_column_int(select_stmt, 0);
        }
        if (result != SQLITE_DONE) {
            throw runtime_error("Failed to execute select statement");
        }
        sqlite3_finalize(select_stmt);

        if (id != -1) {
            delete_item(id);
        }
    }

    int add_item(const string& name, int parent_id, int level, int size, int is_file, int is_dir) {
        string sql = "INSERT INTO directory_structure (name, parent_id, level, size, is_file, is_dir) VALUES (?, ?, ?, ?, ?, ?)";
        sqlite3_stmt* stmt;
        int result = sqlite3_prepare_v2(db_, sql.c_str(), sql.length() + 1, &stmt, nullptr);
        if (result != SQLITE_OK) {
            throw runtime_error("Failed to prepare insert statement");
        }
        sqlite3_bind_text(stmt, 1, name.c_str(), name.length(), nullptr);
        sqlite3_bind_int(stmt, 2, parent_id);
        sqlite3_bind_int(stmt, 3, level);
        sqlite3_bind_int(stmt, 4, size);
        sqlite3_bind_int(stmt, 5, is_file);
        sqlite3_bind_int(stmt, 6, is_dir);
        result = sqlite3_step(stmt);
        if (result != SQLITE_DONE) {
            throw runtime_error("Failed to execute insert statement");
        }
        int id = sqlite3_last_insert_rowid(db_);
        sqlite3_finalize(stmt);

        return id;
    }

    int add_or_update_database(const string& name, int parent_id, int level) {
        // Check if the directory already exists
        string select_sql = "SELECT id FROM directory_structure WHERE name = ? AND parent_id = ? AND level = ? AND is_dir = 1";
        sqlite3_stmt* select_stmt;
        int result = sqlite3_prepare_v2(db_, select_sql.c_str(), select_sql.length() + 1, &select_stmt, nullptr);
        sqlite3_bind_text(select_stmt, 1, name.c_str(), name.length(), nullptr);
        sqlite3_bind_int(select_stmt, 2, parent_id);
        sqlite3_bind_int(select_stmt, 3, level);
        if (result != SQLITE_OK) {
            throw runtime_error("Failed to prepare select statement");
        }
        int id = -1;
        if ((result = sqlite3_step(select_stmt)) == SQLITE_ROW) {
            id = sqlite3_column_int(select_stmt, 0);
        }
        if (result != SQLITE_DONE) {
            throw runtime_error("Failed to execute select statement");
        }
        sqlite3_finalize(select_stmt);

        if (id == -1) { // Need to insert new directory into database
            id = add_directory(name, parent_id, level);
        }

        return id;
    }

    vector<string> split(const string& s, char delimiter) {
        vector<string> tokens;
        string token;
