int scanDirectory(const char* path, int parent_id, int level, sqlite3* db) {
    FileInfo info;
    HANDLE hFind;
    WIN32_FIND_DATA findData;
    string newPath;

    hFind = FindFirstFile(path, &findData);
    if (hFind == INVALID_HANDLE_VALUE) {
        cerr << "Failed to scan directory: " << path << endl;
        return -1;
    }

    do {
        if (strcmp(findData.cFileName, ".") != 0 && strcmp(findData.cFileName, "..") != 0) {
            info.name = findData.cFileName;
            newPath = string(path) + "\\" + string(findData.cFileName);
            info.is_dir = (findData.dwFileAttributes & FILE_ATTRIBUTE_DIRECTORY) != 0;
            info.size = (DWORD64)findData.nFileSizeHigh << 32 | findData.nFileSizeLow;
            info.parent_id = parent_id;
            info.level = level;

