int add_file(const std::string& db_path, const std::string& file_path, int parent_directory)
{
    int ret = 0;
    sqlite3* db;
    if (sqlite3_open(db_path.c_str(), &db))
    {
        std::cerr << "Cannot open database: " << sqlite3_errmsg(db) << std::endl;
        return 1;
    }

    namespace fs = std::filesystem;
    fs::path path(file_path);
    int64_t size;

    if (fs::is_regular_file(path))
    {
        size = fs::file_size(path);
    }
    else
    {
        std::cerr << "Path is not a file: " << file_path << std::endl;
        sqlite3_close(db);
        return 1;
    }

    std::string name = path.filename().string();

    sqlite3_stmt* stmt;
    std::string insert_sql = "INSERT INTO files (name, size, parent_directory) VALUES (?, ?, ?);";
    sqlite3_prepare_v2(db, insert_sql.c_str(), insert_sql.size(), &stmt, nullptr);

    sqlite3_bind_text(stmt, 1, name.c_str(), name.size(), SQLITE_TRANSIENT);
    sqlite3_bind_int64(stmt, 2, size);
    sqlite3_bind_int(stmt, 3, parent_directory);

    if (sqlite3_step(stmt) != SQLITE_DONE)
    {
        std::cerr << "Error adding file: " << sqlite3_errmsg(db) << std::endl;
        ret = 1;
    }

    sqlite3_finalize(stmt);
    sqlite3_close(db);
    return ret;
}

int remove_file(const std::string& db_path, int file_id)
{
    int ret = 0;
    sqlite3* db;

    if (sqlite3_open(db_path.c_str(), &db))
    {
        std::cerr << "Cannot open database: " << sqlite3_errmsg(db) << std::endl;
        return 1;
    }

    std::string delete_sql = "DELETE FROM files WHERE id = ?;";
    sqlite3_stmt* stmt;

    sqlite3_prepare_v2(db, delete_sql.c_str(), delete_sql.size(), &stmt, nullptr);
    sqlite3_bind_int(stmt, 1, file_id);

    if (sqlite3_step(stmt) != SQLITE_DONE)
    {
        std::cerr << "Error removing file: " << sqlite3_errmsg(db) << std::endl;
        ret = 1;
    }

    sqlite3_finalize(stmt);
    sqlite3_close(db);
    return ret;
}
