    <script>
        let dataCSV = [];
        let beritaIndex = -1;

        function uraikanCSV(csv) {
            let data = Papa.parse(csv, {
                header: true,
                skipEmptyLines: true,
                delimiter: ';' // Gunakan ; sebagai pemisah
            }).data;

            // Acak urutan data
            for (let i = data.length - 1; i > 0; i--) {
                let j = Math.floor(Math.random() * (i + 1));
                let temp = data[i];
                data[i] = data[j];
                data[j] = temp;
