```csharp
using SQLite;
using System.Collections.Generic;
using System.Threading.Tasks;

public class SqLiteDatabase
{
    private readonly SQLiteAsyncConnection _connection;

    public SqLiteDatabase(string databaseName)
    {
        _connection = new SQLiteAsyncConnection(databaseName + ".db");
        InitializeDatabase();
    }

    private async void InitializeDatabase()
    {
        await _connection.CreateTableAsync<User>();
        await _connection.CreateTableAsync<Card>();
        await _connection.CreateTableAsync<UserCard>();
    }

    public async Task<User> GetOrAddUserAsync(string userId)
    {
        var user = await _connection.FindAsync<User>(userId);
        if (user != null) return user;

        user = new User { Id = userId };
        await AddOrUpdateUserAsync(user);
        return user;
    }

    public async Task<User> GetUserAsync(string userId)
    {
        var user = await _connection.FindAsync<User>(userId);
        if (user == null) return null;

        user.Cards = await GetUserCards(userId);
        return user;
    }

    public async Task AddOrUpdateUserAsync(User user)
    {
        await _connection.InsertOrReplaceAsync(user);

        foreach (var userCard in user.Cards)
        {
            await AddCardIfNotExistAsync(userCard.Card);
            await AssignCardToUserAsync(user.Id, userCard.CardId, userCard.Timestamp);
        }
    }

    private async Task<List<UserCard>> GetUserCards(string userId)
    {
        return await _connection.Table<UserCard>().Where(x => x.UserId == userId).ToListAsync();
    }

    public async Task AddCardIfNotExistAsync(Card card)
    {
        await _connection.InsertOrIgnoreAsync(card);
    }

    public async Task<List<Card>> GetAllCards(int limit = 100)
    {
        return await _connection.Table<Card>().Take(limit).ToListAsync();
    }

    public async Task<Card> GetCardAsync(string cardId)
    {
        return await _connection.FindAsync<Card>(cardId);
    }

    public async Task AssignCardToUserAsync(string userId, string cardId, long timestamp)
    {
        var userCard = new UserCard
        {
            UserId = userId,
            CardId = cardId,
            Timestamp = timestamp
        };

        await _connection.InsertOrReplaceAsync(userCard);
    }
}
```

