public class ConfigProvider extends ContentProvider {
    // Путь к базе данных
    private static final String DB_PATH = "/sdcard/configs/";
    private static final String DB_NAME = "configs.db";

    // URI для ContentProvider
    private static final Uri CONTENT_URI = Uri.parse("content://com.example.configprovider/config");

    // Колонки базы данных
    public static final String KEY_ID = "_id";
    public static final String KEY_NAME = "name";

    // Объект базы данных
    private SQLiteDatabase database;

    @Override
    public boolean onCreate() {
        // Создаем базу данных
        database = SQLiteDatabase.openOrCreateDatabase(DB_PATH + DB_NAME, null);
        database.execSQL("CREATE TABLE IF NOT EXISTS configs (_id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT);");

        // Добавляем значения в базу данных
        ContentValues values = new ContentValues();
        values.put(KEY_NAME, "config1");
        database.insert("configs", null, values);

        return true;
    }

    @Nullable
    @Override
    public Cursor query(@NonNull Uri uri, @Nullable String[] projection, @Nullable String selection, @Nullable String[] selectionArgs, @Nullable String sortOrder) {
        // Получаем данные из базы данных
        Cursor cursor = database.query("configs", new String[]{KEY_ID, KEY_NAME}, null, null, null, null, null);
        return cursor;
    }

    @Nullable
    @Override
    public String getType(@NonNull Uri uri) {
        return "text/plain";
    }

    @Nullable
    @Override
    public Uri insert(@NonNull Uri uri, @Nullable ContentValues values) {
        // Добавляем значение в базу данных
        database.insert("configs", null, values);
        return CONTENT_URI;
    }

    @Override
    public int delete(@NonNull Uri uri, @Nullable String selection, @Nullable String[] selectionArgs) {
        // Удаляем значение из базы данных
        int deletedRows = database.delete("configs", KEY_ID + "=?", selectionArgs);
        return deletedRows;
    }

    @Override
    public int update(@NonNull Uri uri, @Nullable ContentValues values, @Nullable String selection, @Nullable String[] selectionArgs) {
        // Обновляем значение в базе данных
        int updatedRows = database.update("configs", values, KEY_ID + "=?", selectionArgs);
        return updatedRows;
    }
}
