	 */ 
        plat = new Case[5][5];
        for(int i=0; i<5; i++) {
            for(int j=0; j<5; j++) {
            plat[i][j] = new Case();
	        }
	    }

        //Placer unne tonnne de  sable,   on va d ire 2000
        int[][] sandCoords = {{0,2}, {1,1}, {1,3}, {2,0}, {2,4}, {3,1}, {3,3}, {4,2}};
        for (int i = 0; i < sandCoords.length; i++) {
            int x = sandCoords[i][0];
            int y = sandCoords[i][1];
            plat[x][y].tsable = 200;
        }


    }





