     */
    public int qteSable(){
        int qte = 0;
        for (int i = 0; i<5; i++){
            for (int j = 0; j<5; j++){
                qte += plat[i][j].tsable;
            }
        }
        return qte;
    }


    public float puissanceO(int Oi, int Oj){
        oeuil o = (oeuil) plat[Oi][Oj];
        return o.puissance;
    }

