        return (this.qteSable()>= 43) || (this.puissanceO(Oi, Oj)>=7);
    }



    public void TDechaine(int Oi, int Oj){
        float incr = 1/2;
        oeuil o = (oeuil) plat[Oi][Oj];
        o.addPuissance(incr);
    }

    public void souffle(int Oi, int Oj){
		int puiss = (int)(Math.random() * ((3 - 1) + 1)) + 1;
        int dir = (int)(Math.random() * (4 + 1));
        oeuil o = (oeuil) plat[Oi][Oj];
        o.addPuissance((float) puiss);
        switch(dir){
            case 0: 
            if(Oi > 0){ // Verifiee si on peut déplacer la case vers le haut
                Case temp = plat[Oi][Oj];
                plat[Oi][Oj] = plat[Oi-1][Oj];
                plat[Oi-1][Oj] = temp;
                this.Oui = Oi-1;}
                // Est-ce qu'il faut ajouter break; ici ? comme ça, on évite de continuer à executer les autres cas :o
            case 1:
            if(Oj < this.plat[0].length - 1){ // Verifie  si on peut déplacer la case vers la droite
                Case temp1 = plat[Oi][Oj];
                plat[Oi][Oj] = plat[Oi][Oj+1];
                plat[Oi][Oj+1] = temp1;
                this.Ouj = Oj-1;}
                //break;
            case 2:
            if(Oi < this.plat.length - 1){ // Vérifier si on peut déplacer la case vers le bas
                Case temp2 = plat[Oi][Oj];
                plat[Oi][Oj] = plat[Oi+1][Oj];
                plat[Oi+1][Oj] = temp2;
                this.Oui = Oi+1;}
                //break;
            case 3:
            if(Oj > 0){ // Verifie si on peut se déplacer vers la gauche
                Case temp3 = plat[Oi][Oj];
                plat[Oi][Oj] = plat[Oi][Oj-1];
                plat[Oi][Oj-1] = temp3;
            }
            //break;

        }
    }

    public void finDeTour(int Oi, int Oj) {
        int quoi = (int)(Math.random() * (1 + 1));
        if (quoi == 0){
            this.souffle(Oi, Oj);
        }
        else{
            this.TDechaine(Oi, Oj);
        }
	    notifyObservers();
    }

    /**
     * Une méthode pour renvoyer la Case aux coordonnées choisies (sera
     * utilisée par la vue).
     */
    public Case getCase(int x, int y) {
	return plat[x][y];
    }
    /**
     * Notez qu'à l'intérieur de la classe [plateau], la classe interne est
     * connue sous le nom abrégé [Case].
     * Son nom complet est [plateau.Case], et cette version complète est
     * la seule à pouvoir être utilisée depuis l'extérieur de [plateau].
     * Dans [plateau], les deux fonctionnent.
     */
}

/** Fin de la classe plateau. */

