/**
 * La vue : l'interface avec l'utilisateur.
 *
 * On définit une classe chapeau [CVue] qui crée la fenêtre principale de 
 * l'application et contient les deux parties principales de notre vue :
 *  - Une zone d'affichage où on voit l'ensemble des plat.
 *  - Une zone de commande avec un bouton pour passer à la génération suivante.
 */
class CVue {
    /**
     * JFrame est une classe fournie pas Swing. Elle représente la fenêtre
     * de l'application graphique.
     */
    private JFrame frame;
    /**
     * VueGrille et VueCommandes sont deux classes définies plus loin, pour
     * nos deux parties de l'interface graphique.
     */
    private VueGrille grille;
    private VueCommandes commandes;

    /** Construction d'une vue attachée à un modèle. */
    public CVue(Plateau modele) {
	/** Définition de la fenêtre principale. */
	frame = new JFrame();
	frame.setTitle("Plateau du desert interdit");
	/**
	 * On précise un mode pour disposer les différents éléments à
	 * l'intérieur de la fenêtre. Quelques possibilités sont :
	 *  - BorderLayout (défaut pour la classe JFrame) : chaque élément est
	 *    disposé au centre ou le long d'un bord.
	 *  - FlowLayout (défaut pour un JPanel) : les éléments sont disposés
	 *    l'un à la suite de l'autre, dans l'ordre de leur ajout, les lignes
	 *    se formant de gauche à droite et de haut en bas. Un élément peut
	 *    passer à la ligne lorsque l'on redimensionne la fenêtre.
	 *  - GridLayout : les éléments sont disposés l'un à la suite de
	 *    l'autre sur une grille avec un nombre de lignes et un nombre de
	 *    colonnes définis par le programmeur, dont toutes les cases ont la
	 *    même dimension. Cette dimension est calculée en fonction du
	 *    nombre de cases à placer et de la dimension du contenant.
	 */
	frame.setLayout(new FlowLayout());

	/** Définition des deux vues et ajout à la fenêtre. */
	grille = new VueGrille(modele);
	frame.add(grille);
	commandes = new VueCommandes(modele);
	frame.add(commandes);
	/**
	 * Remarque : on peut passer à la méthode [add] des paramètres
	 * supplémentaires indiquant où placer l'élément. Par exemple, si on
	 * avait conservé la disposition par défaut [BorderLayout], on aurait
	 * pu écrire le code suivant pour placer la grille à gauche et les
	 * commandes à droite.
	 *     frame.add(grille, BorderLayout.WEST);
	 *     frame.add(commandes, BorderLayout.EAST);
	 */

	/**
	 * Fin de la plomberie :
	 *  - Ajustement de la taille de la fenêtre en fonction du contenu.
	 *  - Indiquer qu'on quitte l'application si la fenêtre est fermée.
	 *  - Préciser que la fenêtre doit bien apparaître à l'écran.
	 */
	frame.pack();
	frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
	frame.setVisible(true);
    }
}

/** La vue grille est responsable de l'affichage des plat. */
class VueGrille extends JPanel implements Observer {
    /** On garde une référence vers le modèle, qu'on doit régulièrement consulter. */
    private Plateau modele;

    /**
     * Constructeur : on garde une référence vers le modèle, puis on se met
     * à l'écoute de celui-ci.
     */
    public VueGrille(Plateau modele) {
	this.modele = modele;
	modele.addObserver(this);
    }

    /**
     * Lorsque la vue est avertie d'un changement dans le modèle, elle se
     * met à jour.
     * Ici, comme l'affichage est très simple, on se contente de demander un
     * nouvel affichage dans le panel.
     */
    public void update() {
	repaint();
    }

    /** À chaque demande d'affichage, la méthode [paintComponent] est exécutée. */
    protected void paintComponent(Graphics g) {
	super.paintComponent(g);
	/** On parcourt l'ensemble des plat en demandant leur affichage un par un. */
	for(int i=0; i<5; i++) {
	    for(int j=0; j<5; j++) {
		dessineCase(g, modele.getCase(i,j), i, j);
	    }
	}
    }

    /**
     * Méthode auxiliaire chargée d'afficher une Case.
     * Cette méthode sait dessiner une Case aux coordonnées pointées par le
     * modèle. Le dernier argument donne l'échelle de la grille (la taille
     * des cases).
     */
    private void dessineCase(Graphics g, Case c, int x, int y) {
	// Taille de la fenêtre de dessin.
	int width = getWidth();
	int height = getHeight();

	// Taille d'une case.
	int wCase = width / 5;
	int hCase = height / 5;
	g.drawRect(x*wCase, y*hCase, wCase-1, hCase-1);
	g.drawString(Integer.toString(c.getTsable()), x*wCase+(wCase/2)-3, y*hCase+(hCase/2)+3);
    }

    /** Cette classe ne gère aucun événement. */
}

/**
 * La vue commande est la zone où on affiche le bouton pour passer à la génération suivante.
 */
class VueCommandes extends JPanel {
    /** On garde une référence vers le modèle, même si ce n'est pas vraiment utile ici. */
    private Plateau modele;

    /**
     * Constructeur : on garde une référence vers le modèle.
     * Puis on crée le bouton, et on le place dans la vue.
     */
    public VueCommandes(Plateau modele) {
	this.modele = modele;

	JButton boutonSuivant = new JButton("Tour suivant");
	boutonSuivant.addActionListener(new ActionListener() {
		public void actionPerformed(ActionEvent e) {
		    modele.finDeTour(modele.Oui, modele.Ouj);
		}
	    });
	add(boutonSuivant);
    }
