import java.util.Random;

public class Desert {
    private Case[][] cases;
    private final int taille;

    public Desert(int taille) {
        this.taille = taille;
        this.cases = new Case[taille][taille];

        // Initialisation des cases
        Random random = new Random();
        for (int i = 0; i < taille; i++) {
            for (int j = 0; j < taille; j++) {
                if (random.nextDouble() <= 0.4) {
                    this.cases[i][j] = new Case();
                } else {
                    this.cases[i][j] = new ZoneQuelconque();
                }
            }
        }

        // Initialisation de l'oeuil
        int x = random.nextInt(taille);
        int y = random.nextInt(taille);
        this.cases[x][y] = new Oeuil();
    }

    public int getTaille() {
        return this.taille;
    }

    public Case getCase(int x, int y) {
        return this.cases[x][y];
    }

    public Oeuil getOeuil() {
        for (int i = 0; i < taille; i++) {
            for (int j = 0; j < taille; j++) {
                if (this.cases[i][j] instanceof Oeuil) {
                    return (Oeuil) this.cases[i][j];
                }
            }
        }
        return null; // Normalement, il y a toujours un oeuil dans le désert
    }

    @Override
    public String toString() {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < taille; i++) {
            for (int j = 0; j < taille; j++) {
                sb.append(cases[i][j].toString());
                sb.append(" ");
            }
            sb.append("\n");
        }
        return sb.toString();
    }
}
