```
private float[][] gaussianMask(int rows, int columns) {
    float[][] mask = new float[rows][columns];
    float sigma = rows / 6.0f;
    float sumTotal = 0.0f;

    for (int row = 0 ; row < rows ; row++) {
        for (int col = 0 ; col < columns ; col++) {
            float xDistance = col - columns / 2.0f;
            float yDistance = row - rows / 2.0f;
            float pixelValue = (float) Math.exp(-(xDistance*xDistance + yDistance*yDistance)/(2.0f*sigma*sigma));
            mask[row][col] = pixelValue;
            sumTotal += pixelValue;
        }
    }

    for (int row = 0 ; row < rows ; row++) {
        for (int col = 0 ; col < columns ; col++) {
            mask[row][col] = mask[row][col]/sumTotal;
        }
    }

    return mask;
}


private BufferedImage applyMask(BufferedImage image, float[][] mask) {
    int width = image.getWidth();
    int height = image.getHeight();
    BufferedImage result = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);
    int[] pixels = image.getRGB(0, 0, width, height, null, 0, width);

    for (int row = 0 ; row < height ; row++) {
        for (int col = 0 ; col < width ; col++) {
            int pixelIndex = row * width + col;
            float redTotal = 0, greenTotal = 0, blueTotal = 0;
            int maskRow = 0;
            for (int relativeRow = -mask.length/2 ; relativeRow <= mask.length/2 ; relativeRow++) {
                int maskCol = 0;
                for (int relativeCol = -mask[0].length/2 ; relativeCol <= mask[0].length/2 ; relativeCol++) {
                    int imageRow = row + relativeRow;
                    int imageCol = col + relativeCol;
                    if (imageRow >= 0 && imageRow < height && imageCol >= 0 && imageCol < width) {
                        int imagePixelIndex = imageRow * width + imageCol;
                        int color = pixels[imagePixelIndex];
                        int red = (color >> 16) & 0xff;
                        int green = (color >> 8) & 0xff;
                        int blue = color & 0xff;
                        redTotal += mask[maskRow][maskCol] * red;
                        greenTotal += mask[maskRow][maskCol] * green;
                        blueTotal += mask[maskRow][maskCol] * blue;
                    }
                    maskCol++;
                }
                maskRow++;
            }
            int newColor = (((int)redTotal) << 16) | (((int)greenTotal) << 8) | ((int)blueTotal);
            result.setRGB(col, row, newColor);
        }
    }

    return result;
}
