// Returns the histogram of the specified image
public static int[] getImageHistogram(BufferedImage image) {
    int[] histogram = new int[256];
    for (int y = 0; y < image.getHeight(); y++) {
        for (int x = 0; x < image.getWidth(); x++) {
            int rgb = image.getRGB(x, y);
            int gray = (int) (0.3 * ((rgb >> 16) & 0xFF) + 0.59 * ((rgb >> 8) & 0xFF) + 0.11 * (rgb & 0xFF));
            histogram[gray]++;
        }
    }
    return histogram;
}

// Creates a JPanel with a histogram graph and title
public static JPanel createHistogramPanel(int[] histogram, String title) {
    int maxValue = 0;
    for (int i = 0; i < histogram.length; i++) {
        if (histogram[i] > maxValue) {
            maxValue = histogram[i];
        }
    }

    JPanel panel = new JPanel();
    panel.setBorder(BorderFactory.createTitledBorder(title));
    panel.setPreferredSize(new Dimension(300, 200));

    JPanel graph = new JPanel() {
        @Override
        protected void paintComponent(Graphics g) {
            super.paintComponent(g);
            Graphics2D g2d = (Graphics2D) g;
            g2d.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);

            int barWidth = getWidth() / histogram.length;
            int maxValueHeight = getHeight() - 20;

            for (int i = 0; i < histogram.length; i++) {
                int barHeight = (int) (((double) histogram[i] / (double) maxValue) * (double) maxValueHeight);
                g2d.setColor(new Color(i, i, i));
                g2d.fillRect(i * barWidth, maxValueHeight - barHeight, barWidth, barHeight);
            }
        }
    };
    graph.setPreferredSize(new Dimension(280, 160));
    panel.add(graph);

    return panel;
}

