                .pickerStyle(SegmentedPickerStyle())
            }

            VStack {
                Text("Setting:")
                Picker(selection: $selectedSetting, label: Text("")) {
                    Text("Beach").tag("beach")
                    Text("Castle").tag("castle")
                    Text("Space Station").tag("space")
