    _selectedMailItem = selectedMailItem;
    emailContentTextBox.Text = selectedMailItem.Body;
    LoadAffectedClients("customers.csv");
    AuthenticateAndInitializeGraphClient().ConfigureAwait(false);
}

private void LoadAffectedClients(string clientsFilePath)
{
    if (File.Exists(clientsFilePath))
    {
        using (var reader = new StreamReader(clientsFilePath))
        {
            while (!reader.EndOfStream)
            {
                var line = reader.ReadLine();
                if (!string.IsNullOrEmpty(line))
                {
                    affectedClientsListBox.Items.Add(line);
                }
            }
        }
    }
    else
    {
        MessageBox.Show("Error: Unable to load clients list from the CSV file.");
    }
}
```

