% Define the function for the Thomas Algorithm
function x = thomas_algorithm(A, B)
    n = length(B);
    x = zeros(n, 1);
    c = zeros(n-1, 1);
    d = zeros(n, 1);

    % Forward substitution
    d(1) = B(1) / A(1, 1);
    c(1) = A(1, 2) / A(1, 1);

    for i = 2:n-1
        temp = (A(i, i) - A(i, i-1) * c(i-1));
        d(i) = (B(i) - A(i, i-1) * d(i-1)) / temp;
        c(i) = A(i, i+1) / temp;
    end

    % Backward substitution
    x(n) = (B(n) - A(n, n-1) * d(n-1)) / (A(n, n) - A(n, n-1) * c(n-1));

    for i = n-1:-1:1
        x(i) = d(i) - c(i) * x(i+1);
    end
end

% Plot the obtained solution
bar(x);
title('Solution of the System of Linear Equations using Thomas Algorithm');
xlabel('Variables (x1, x2, x3, x4, x5)');
ylabel('Values');
xticklabels({'x1', 'x2', 'x3', 'x4', 'x5'});
