x1 = input('请输入第一个点的x坐标：');
y1 = input('请输入第一个点的y坐标：');
z1 = input('请输入第一个点的z坐标：');
x2 = input('请输入第二个点的x坐标：');
y2 = input('请输入第二个点的y坐标：');
z2 = input('请输入第二个点的z坐标：');

% 计算直线方程
syms t;
x = x1 + t * (x2 - x1);
y = y1 + t * (y2 - y1);
z = z1 + t * (z2 - z1);

% 判断直线与哪个坐标面相交
if x2 - x1 == 0
    disp('直线与yz平面相交！');
    yz_x = x1;
    yz_y = subs(y, t, 0);
    yz_z = subs(z, t, 0);
    fprintf('交点坐标为 (%.2f, %.2f, %.2f)\n', yz_x, yz_y, yz_z);
elseif y2 - y1 == 0
    disp('直线与xz平面相交！');
    xz_x = subs(x, t, 0);
    xz_y = y1;
    xz_z = subs(z, t, 0);
    fprintf('交点坐标为 (%.2f, %.2f, %.2f)\n', xz_x, xz_y, xz_z);
elseif z2 - z1 == 0
    disp('直线与xy平面相交！');
    xy_x = subs(x, t, 0);
    xy_y = subs(y, t, 0);
    xy_z = z1;
    fprintf('交点坐标为 (%.2f, %.2f, %.2f)\n', xy_x, xy_y, xy_z);
else
    disp('直线与坐标轴平行，无法判断！');
end

% 输出直线方程
