```R
# Log-likelihood function for Beta distribution
log.likelihood.beta <- function(parameters, obs) {
  alpha <- parameters[1]
  beta <- parameters[2]

  if (alpha <= 0 || beta <= 0) {
    return(-Inf)
  }

  n <- length(obs)

  log.likelihood <- n*(lgamma(alpha+beta) - lgamma(alpha)) 
  log.likelihood <- log.likelihood + sum((alpha - 1) * log(obs) + (beta - 1) * log(1 - obs))

  return(log.likelihood)
}

# Run Newton-Raphson Algorithm
find_mle_NR <- function(obs) {
  init <- c(1, 1)
  res <- optim(init, log.likelihood.beta, control = list(fnscale = -1), obs = obs, method = "BFGS", hessian = TRUE)
  mle <- res$par

  return(mle)
}

# Get the observations
obs <- c(0.45, 0.62, 0.78, 0.73, 0.59, 0.55, 0.68, 0.69)

# Find the MLEs of alpha and beta
mle <- find_mle_NR(obs)
cat("Alpha MLE:", mle[1], "\n")
cat("Beta MLE:", mle[2], "\n")
```

