library("numDeriv")

# Log-likelihood function for Beta distribution
log.likelihood.beta <- function(parameters, obs) {
  alpha <- parameters[1]
  beta <- parameters[2]

  if (alpha <= 0 || beta <= 0) {
    return(-Inf)
  }

  n <- length(obs)
  log.likelihood <- n * (lgamma(alpha + beta) - lgamma(alpha))
  log.likelihood <- log.likelihood + sum((alpha - 1) * log(obs) + (beta - 1) * log(1 - obs))

  return(log.likelihood)
}

# Newton-Raphson algorithm
find_mle_NR <- function(obs, init = c(1, 1), tol = 1e-8, max_iter = 100) {
  par <- init
  iter <- 1
  converged <- FALSE

  while (iter <= max_iter && !converged) {
    # Calculate gradient and Hessian
    grad <- grad(log.likelihood.beta, par, obs = obs)
    hess <- hessian(log.likelihood.beta, par, obs = obs)

    # Check if Hessian is invertible
    if (det(hess) == 0) {
      cat("Warning: Hessian is not invertible\n")
      break
    }

    # Update parameters
    par_new <- par - solve(hess) %*% grad

    # Check convergence
    if (all(abs(par_new - par) < tol)) {
      converged <- TRUE
    }

    par <- par_new
    iter <- iter + 1
  }

  if (!converged) {
    cat("Warning: Didn't converge in", max_iter, "iterations\n")
  }

  return(par)
}

