bool isDivisibleBy7(int num) {
    // Base cases
    if (num == 0) return true;
    if (num == 7) return true;
    if (num < 14) return false;

    // Remove the last digit and double it
    int last_digit_removed_and_doubled = (num % 10) * 2;

