    int x = 0;
    int y = radius;
    int delta = 1 - 2 * radius;
    int error = 0;

    while (y >= 0)
    {
        g.FillRectangle(Brushes.Black, x1 + x, y1 + y, 1, 1);
        g.FillRectangle(Brushes.Black, x1 + x, y1 - y, 1, 1);
        g.FillRectangle(Brushes.Black, x1 - x, y1 + y, 1, 1);
        g.FillRectangle(Brushes.Black, x1 - x, y1 - y, 1, 1);

        error = 2 * (delta + y) - 1;
        if (delta < 0 && error <= 0)
        {
            delta += 2 * ++x + 1;
            continue;
        }
        error = 2 * (delta - x) - 1;
        if (delta > 0 && error > 0)
        {
            delta += 1 - 2 * --y;
            continue;
        }
        x++;
        delta += 2 * (x - y);
        y--;
    }
