function [T, theta, omega] = pendulumSolver(tSpan, dt, theta0, omega0, g, L)
    % Initialize time domain
    t0 = tSpan(1);
    tf = tSpan(2);
    T = t0:dt:tf;

    % Initialize solution arrays
    n = length(T);
    theta = zeros(1, n);
    omega = zeros(1, n);

    % Set initial conditions
    theta(1) = theta0;
    omega(1) = omega0;

    % Define function
    f = @(theta, omega) [omega; -(g/L)*sin(theta)];

    % Iterate through time steps
    for i=1:n-1
        yi = [theta(i); omega(i)];

        % Calculate slopes
        k1 = f(yi(1), yi(2));
        k2 = f(yi(1) + dt/2*k1(1), yi(2) + dt/2*k1(2));
        k3 = f(yi(1) + dt/2*k2(1), yi(2) + dt/2*k2(2));
        k4 = f(yi(1) + dt*k3(1), yi(2) + dt*k3(2));

        % Update solution
        yi_new = yi + dt/6 * (k1 + 2*k2 + 2*k3 + k4);
        theta(i+1) = yi_new(1);
        omega(i+1) = yi_new(2);
    end
end
