function [x, err, fig] = solve_LS(A, b)

  % Check if A is a square matrix
  if size(A,1) == size(A,2)
      % Check if A is invertible (i.e. det(A) != 0)
      if det(A) ~= 0
          % If A is invertible, we can solve for x directly
          x = A\b;
      else
          % If A is not invertible, we find a least squares solution
          x = (A' * A) \ (A' * b);
      end
  else
      % If A is not a square matrix, we find a least squares solution
      x = (A' * A) \ (A' * b);
  end

  err = A*x - b;

  fig = figure;

  % Plot: black dots
  plot(x, err, 'k.', 'MarkerSize', 20);
  hold on;

  % Plot: blue lines (you may need a for loop)
  for i = 1:length(err)
      plot([x(i) x(i)], [err(i) 0], 'b:');
  end

  % Plot: red dotted line
  yline(0, 'r--');

  % Set axis limits
  xlim([min(x)-1 max(x)+1]);
  ylim([min(err)-1 max(err)+1]);

  % Label x-axis, y-axis, and title
  xlabel('x');
  ylabel('Ax-b');
  title('Error computed from Ax-b');

end

