public Result<Map<String, String>> getData(Map<String, String> paramMap) {
        // 1 Param checked
        if (!ProviderParamCheck.checkBlank(this, paramMap)) {
            return Result.errorResult(ErrorCode.ERR_PARAM);
        }
        // 2. 调用UIC服务获取用户信息
        String clickUserIdListStr = paramMap.get(FeatureKeys.CLICK_USER_ID_LIST.getKey());
        String firstTenClickUserIdListStr = getFirstTenElements(clickUserIdListStr);
        ResultDO<List<BaseUserDO>> userInfoListResultDO;
        try {
            userInfoListResultDO = uicReadServiceClient.getUserInfoListByUserIds(firstTenClickUserIdListStr);
        } catch (Exception e) {
            log.error("UIC query exception", e);
            return Result.errorResult(ErrorCode.ERR_HSF);
        }
        //3. 检查UIC服务调用结果
        if (userInfoListResultDO == null || !userInfoListResultDO.isSuccess() || userInfoListResultDO.getModule() == null) {
            return Result.errorResult(ErrorCode.ERR_HSF_RESULT);
        }
        // 4. 检查是否有天猫商家
        boolean hasB2CShop = false;
        List<BaseUserDO> userDOS = userInfoListResultDO.getModule();
        for (BaseUserDO userDO : userDOS) {
            //list中可能存在null，表示用户不存在
            if (userDO != null && UserUtils.isBSeller(userDO)) {
                hasB2CShop = true;
                break;
            }
        }
        // 5. 返回结果
        Map<String, String> resultMap = new HashMap<>(1);
        resultMap.put(FeatureKeys.CLICK_SHOP_LIST_IS_B2C.getKey(), String.valueOf(hasB2CShop));
        return Result.succResult(resultMap);
    }

public Result<Map<String, String>> getData(Map<String, String> paramMap) {
        // 1. 参数检查
        if (ProviderParamCheck.checkBlank(this, paramMap)) {
            // 2. 调用UIC服务获取用户信息
            String clickUserIdListStr = paramMap.get(FeatureKeys.CLICK_USER_ID_LIST.getKey());
            String firstTenClickUserIdListStr = getFirstTenElements(clickUserIdListStr);
            ResultDO<List<BaseUserDO>> userInfoListResultDO = null;
            try {
                userInfoListResultDO = uicReadServiceClient.getUserInfoListByUserIds(firstTenClickUserIdListStr);
            } catch (Exception e) {
                log.error("UIC query exception", e);
                return Result.errorResult(ErrorCode.ERR_HSF);
            }
            // 3. 检查UIC服务调用结果
            if (userInfoListResultDO == null || !userInfoListResultDO.isSuccess() || userInfoListResultDO.getModule() == null) {
                return Result.errorResult(ErrorCode.ERR_HSF_RESULT);
            }
            // 4. 检查是否有天猫商家
            boolean hasB2CShop = userInfoListResultDO.getModule().stream()
                    .filter(Objects::nonNull)
                    .anyMatch(UserUtils::isBSeller);
            // 5. 返回结果
            Map<String, String> resultMap = new HashMap<>(1);
            resultMap.put(FeatureKeys.CLICK_SHOP_LIST_IS_B2C.getKey(), String.valueOf(hasB2CShop));
            return Result.succResult(resultMap);
        } else {
            return Result.errorResult(ErrorCode.ERR_PARAM);
        }
    }

