function rollAbilityScore() {
  const roll = () => Math.floor(Math.random() * 6) + 1;
  const rolls = [roll(), roll(), roll(), roll()];
  rolls.sort((a, b) => b - a);
  rolls.pop();
  return rolls.reduce((sum, roll) => sum + roll, 0);
}

// Function to generate an ability score set
function generateAbilityScores() {
  return {
    strength: rollAbilityScore(),
    dexterity: rollAbilityScore(),
    constitution: rollAbilityScore(),
    intelligence: rollAbilityScore(),
    wisdom: rollAbilityScore(),
    charisma: rollAbilityScore(),
  };
}

// Generate random race and class index
const raceIndex = Math.floor(Math.random() * races.length);
const classIndex = Math.floor(Math.random() * classes.length);

// Build random character
