import java.util.Random;
import java.util.Scanner;
import javax.swing.*;
import java.awt.*;
import java.net.*;
import java.text.*;
import java.time.*;
import java.util.*;
import java.io.*;

public class RandomSequence {
  public static void main(String[] args) throws Exception {

    Random random = new Random();

    // Randomly choose a type of vegetable from an array and print it out
    String[] vegetables = {"Carrots", "Broccoli", "Cauliflower", "Kale"};
    String randomVeggie = vegetables[random.nextInt(vegetables.length)];
    System.out.println("Today's vegetable is: " + randomVeggie);

    // Generate a random number and use it to set the frame size of a JFrame and add a JLabel to it
    JFrame frame = new JFrame("Random Frame");
    int frameSize = random.nextInt(400) + 200;
    frame.setSize(frameSize, frameSize);
    frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
    JLabel label = new JLabel(randomVeggie);
    frame.add(label);
    frame.setVisible(true);

    // Create a random date within the last year and display it using SimpleDateFormat
    int year = 2021;
    int dayOfYear = random.nextInt(365) + 1;
    LocalDate date = LocalDate.ofYearDay(year, dayOfYear);
    DateFormat dateFormat = new SimpleDateFormat("yyyy/MM/dd");
    System.out.println("Today's date is: " + dateFormat.format(date));

    // Use a switch statement to randomly choose a geometric shape and calculate its area
    int side1 = random.nextInt(10) + 1;
    int side2 = random.nextInt(10) + 1;
    double area = 0;
    int shape = random.nextInt(3);
    switch(shape) {
      case 0:
        area = side1 * side2;
        System.out.println("The area of the rectangle is: " + area);
        break;
      case 1:
        area = Math.PI * Math.pow(side1, 2);
        System.out.println("The area of the circle is: " + area);
        break;
      case 2:
        area = (side1 * side2) / 2.0;
        System.out.println("The area of the triangle is: " + area);
        break;
      default:
        System.out.println("No shape chosen.");
    }

    // Use a while loop to print out random numbers until a positive multiple of 5 is reached
    int number = 0;
    while(number % 5 != 0 || number <= 0) {
      number = random.nextInt(100) - 50;
      System.out.println("Random number: " + number);
    }

    // Create a random password generator using a StringBuilder and for loop
    String[] possibleChars = {"abcdefghijklmnopqrstuvwxyz", "ABCDEFGHIJKLMNOPQRSTUVWXYZ", "0123456789", "!@#$%^&*()"};
    StringBuilder password = new StringBuilder();
    for(int i = 0; i < 10; i++) {
      int charGroup = random.nextInt(possibleChars.length);
      int charIndex = random.nextInt(possibleChars[charGroup].length());
      password.append(possibleChars[charGroup].charAt(charIndex));
    }
    System.out.println("Generated password: " + password.toString());

    // Use a do-while loop to ask the user for an input until the input is "quit"
    Scanner scanner = new Scanner(System.in);
    String input = "";
    do {
      System.out.println("Enter something (or 'quit' to exit): ");
      input = scanner.nextLine();
      System.out.println("You entered: " + input);
    } while(!input.equals("quit"));

    // Write a random sentence to file and read it back using FileReader and BufferedReader
    String[] words = {"The", "quick", "brown", "fox", "jumps", "over", "the", "lazy", "dog"};
    File file = new File("random-sentence.txt");
    FileWriter writer = new FileWriter(file);
    for(int i = 0; i < 10; i++) {
      int wordIndex = random.nextInt(words.length);
      writer.write(words[wordIndex] + " ");
    }
    writer.close();
    FileReader reader = new FileReader(file);
    BufferedReader bufferedReader = new BufferedReader(reader);
    System.out.println("Random sentence from file: " + bufferedReader.readLine());
    bufferedReader.close();
  }
}

