public static async Task<string?> ShowOtpDialog()
{
    var taskCompletionSource = new TaskCompletionSource<string?>();
    using var layoutInflater = LayoutInflater.FromContext(Instance)!;
    using var view = layoutInflater.Inflate(Resource.Layout.OtpDialog, null);

    var otpEditText = view!.FindViewById<EditText>(Resource.Id.dialog_input)!;
    var submitButton = view.FindViewById<Button>(Resource.Id.dialog_submit)!;

    using var dialogBuilder = new AlertDialog.Builder(view.Context)
        .SetTitle("Telegram verification code")
        .SetView(view)
        .SetCancelable(false);

    AlertDialog? dialog = null!;

    submitButton.Click += (_, _) =>
    {
        dialog.Dismiss();
        var otp = otpEditText.Text;
        taskCompletionSource.SetResult(otp);
    };

    await MainThread.InvokeOnMainThreadAsync(() =>
    {
        dialog = dialogBuilder.Create();
        dialog.Show();
    }).ConfigureAwait(false);

    // Wait for the task to be completed
    var task = await taskCompletionSource.Task.ConfigureAwait(false);

    // Dispose
    otpEditText.Dispose();
    submitButton.Dispose();
    dialog.Dispose();
    return task;
}
