    switch (this.arvo) {
      case 1:
        return "A";
      case 11:
        return "J";
      case 12:
        return "Q";
      case 13:
        return "K";
      default:
        return this.arvo;
    }
  }

  toString() {
    return `<span>${this.arvosymboli}${this.maa.symboli}</span>`;
  }
}

