export class Kasi extends Pakka {
  constructor() {
    super();
  }

  static luoKasi() {
    let apukasi = new Kasi();
    return apukasi;
  }

  get kortteja() {
    return this.kortit.length;
  }

  get summa() {
    let summa = 0;
    let aces = 0;
    for (let kortti of this.kortit) {
      if (kortti.arvo === 1) {
        aces++;
      }
      summa += kortti.arvo;
    }
    while (aces > 0 && summa < 12) {
      summa += 10;
      aces--;
    }
    return summa;
  }

  naytaKortit(maara) {
    for (let i = 1; i <= maara; i++) {
      this.kortit[i].naytaKortti();
    }
  }
}

