  paivitaKortit(pelaajaElementit, pelaajankasi.kortit);
  paivitaKortit(jakajaElementit, [jakajankasi.kortit[0], new Kortti(null, null)]); //vain toinen jakajan korteista näkyy alussa
  paivitaTulos();
}

