#include <iostream>
#include <cstdlib>
#include <ctime>

using namespace std;

const int SCREEN_WIDTH = 80;
const int SCREEN_HEIGHT = 25;

class Ball {
    int x, y, dx, dy;
    char shape;
public:
    Ball(char s = 'o') {
        x = rand() % SCREEN_WIDTH;
        y = rand() % SCREEN_HEIGHT;
        dx = rand() % 3 - 1;
        dy = rand() % 3 - 1;
        while (dx == 0 && dy == 0) {
            dx = rand() % 3 - 1;
            dy = rand() % 3 - 1;
        }
        shape = s;
    }
    void move() {
        x += dx;
        y += dy;
        if (x < 0) {
            x = 0;
            dx = -dx;
        } else if (x >= SCREEN_WIDTH) {
            x = SCREEN_WIDTH - 1;
            dx = -dx;
        }
        if (y < 0) {
            y = 0;
            dy = -dy;   
        } else if (y >= SCREEN_HEIGHT) {
            y = SCREEN_HEIGHT - 1;
            dy = -dy;
        }
    }
    void draw() {
        gotoxy(x, y);
        cout << shape;
    }
};

int main() {
    srand(time(NULL));
    Ball ball;
    bool running = true;
    while (running) {
        ball.draw();
        ball.move();
        if (kbhit()) {
            char c = getch();
            if (c == 'c' || c == 'C') {
                Ball new_ball;
                ball = new_ball;
            } else if (c == 'q' || c == 'Q') {
                running = false;
            }
        }
    }
    return 0;
}

