    }
}

interface Deck {
    int size();
    Card peekTop();
    ArrayDeck copy();
    ArrayDeck outShuffle();
    ArrayDeck inShuffle();
}

class ArrayDeck implements Deck {
    private final Card[] cards;

    public ArrayDeck(int maxRank) {
        String[] suits = {"C", "D", "H", "S"};
        this.cards = new Card[maxRank * suits.length];
        for (int i = 0; i < maxRank; i++) {
            for (int j = 0; j < suits.length; j++) {
                this.cards[i * suits.length + j] = new Card(i + 1, suits[j]);
            }
        }
    }

    private ArrayDeck(Card[] cards) {
        this.cards = cards;
    }

    public int size() {
        return this.cards.length;
    }

    public Card peekTop() {
        return this.cards[0];
    }

    public ArrayDeck copy() {
        return new ArrayDeck(Arrays.copyOf(this.cards, this.cards.length));
    }

    private ArrayDeck shuffle(boolean out) {
        Card[] shuffled = new Card[this.cards.length];
        int half = this.cards.length / 2;
        for (int i = 0; i < half; i++) {
            shuffled[i * 2 + (out ? 0 : 1)] = this.cards[i];
            shuffled[i * 2 + (out ? 1 : 0)] = this.cards[i + half];
        }
        return new ArrayDeck(shuffled);
    }

    public ArrayDeck outShuffle() {
        return shuffle(true);
    }

    public ArrayDeck inShuffle() {
        return shuffle(false);
    }

    public String toString() {
        StringBuilder deckString = new StringBuilder();
        for (int i = 0; i < this.cards.length; i++) {
            deckString.append(this.cards[i]).append(" ");
            if ((i + 1) % 13 == 0) {
                deckString.append("\n");
            }
        }
        return deckString.toString();
    }
}
```

