package com.psl.gems.game;

import java.util.*;
import com.psl.gems.card.*;
import com.psl.gems.player.*;

public class CardGame {
    private PackOfCards pack;
    private List<Player> players;

    public CardGame(){
        pack = new PackOfCards();
        players = new ArrayList<Player>();
    }

    public void register(Player player){
        players.add(player);
    }

    public int getNumberOfPlayers(){
        return players.size();
    }

    public void play(){
        pack.resetSequence(true);
        pack.shuffle();

        for(int i = 0; i < 3; i++){
            for(Player player : players){
                player.addCard(pack.getTopCard());
            }
        }

        Player winner = getWinner();
        System.out.println("Winner is " + winner.getName());
        Card.writeHTML(players);
    }

    private Player getWinner(){
        Player winner = null;
        int highestValue = -1;
        for(Player player : players){
            int playerHighValue = player.getHighestCard().computeCardValue();
            if(playerHighValue > highestValue){
                highestValue = playerHighValue;
                winner = player;
            }
        }
        return winner;
    }
}
