package com.psl.gems.card;

public class Card implements Comparable<Card> {
    private Suit suit;
    private Rank rank;

    public Card(Suit suit, Rank rank){
        this.suit = suit;
        this.rank = rank;
    }

    public int computeCardValue(){
        return rank.getValue() * 4 + suit.getValue();
    }

    @Override
    public int compareTo(Card card) {
        return computeCardValue() - card.computeCardValue();
    }
}
