class Player {
  constructor(name, health, hand) {
    this.name = name;
    this.health = health;
    this.hand = hand;
    this.hasMissed = false;
  }

  hasCard(card) {
    return this.hand.includes(card);
  }

  removeCard(card) {
    this.hand = this.hand.filter(c => c !== card);
  }

  toString() {
