  constructor() {
    super("Bang");
  }

  canBePlayedAgainstTarget(target, attacker, cardsPlayedThisTurn) {
    const misseds = cardsPlayedThisTurn.filter((card) => card.constructor === Missed);
    return target !== attacker && !target.hasMissed() && misseds.length === 0;
  }

