    getline(std::cin, command);

    int fd = shm_open(sharedMemoryName, O_RDWR, 0666);
    char* sharedMemory = (char*)mmap(0, sharedMemorySize, PROT_READ | PROT_WRITE, MAP_SHARED, fd, 0);
    sem_t* semReader = sem_open(semReaderName, 0);
    sem_t* semWriter = sem_open(semWriterName, 0);
    sem_t* semResponse = sem_open(semResponseName, 0);

    sem_wait(semWriter);
    strcpy(sharedMemory, command.c_str());
    sem_post(semWriter);

    if (command.rfind("read", 0) == 0) {
        sem_wait(semResponse);
        sem_wait(semReader);
        std::cout << "Received response from server:\n" << sharedMemory << std::endl;
        memset(sharedMemory, 0, sharedMemorySize);
        sem_post(semReader);
    }

    munmap(sharedMemory, sharedMemorySize);
    close(fd);
    sem_close(semReader);
    sem_close(semWriter);
    sem_close(semResponse);

    return 0;
}
