// Server.cpp

#include <iostream>
#include <fstream>
#include <sstream>
#include <string>
#include <thread>
#include <mutex>
#include <condition_variable>
#include <sys/shm.h>
#include <sys/sem.h>
#include <sys/ipc.h>
#include <sys/types.h>
#include <unistd.h>

std::mutex mtx;
std::condition_variable cv;
std::string data;
bool ready = false;

void process_request() {
    std::unique_lock<std::mutex> lock(mtx);
    cv.wait(lock, []{ return ready; });

    std::istringstream iss(data);
    std::string command, filename;
    iss >> command >> filename;

    if (command == "read") {
        std::ifstream file(filename);
        std::string content((std::istreambuf_iterator<char>(file)),
                            std::istreambuf_iterator<char>());
        data = content;

    } else if (command == "write") {
        std::string file_data;
        std::getline(iss, file_data);
        std::ofstream(file_data);
    }

    ready = false;
    cv.notify_one();
}

int main() {
    // allocating shared memory and semaphore
    int shm_id = shmget((key_t)1234, 1024, 0666 | IPC_CREAT);
    int sem_id = semget((key_t)5678, 1, 0666 | IPC_CREAT);
    char *shared_memory = static_cast<char*>(shmat(shm_id, nullptr, 0));

    // initializing semaphore
    semctl(sem_id, 0, SETVAL, 1);

    while(true) {
        // wait for semaphore
        semop(sem_id, 0, -1);

        // read command from shared memory
        data = shared_memory;

        // clear shared memory
        std::fill_n(shared_memory, 1024, '\0');

        // lock mutex and notify the condition variable
        {
            std::lock_guard<std::mutex> lock(mtx);
            ready = true;
        }
        cv.notify_one();

        // create a new thread for processing the request
        std::thread worker(process_request);
        worker.join();

        // put the result back into shared memory
        std::copy(data.begin(), data.end(), shared_memory);

        // release semaphore
        semop(sem_id, 0, 1);
    }

    // detach and delete shared memory and semaphore
    shmdt(shared_memory);
    shmctl(shm_id, IPC_RMID, nullptr);
    semctl(sem_id, 0, IPC_RMID);

    return 0;
}

// Client.cpp

#include <iostream>
#include <string>
#include <sys/shm.h>
#include <sys/sem.h>
#include <unistd.h>

int main() {
    // locate shared memory and semaphore
    int shm_id = shmget((key_t)1234, 1024, 0666);
    int sem_id = semget((key_t)5678, 1, 0666);
    char *shared_memory = static_cast<char*>(shmat(shm_id, nullptr, 0));

    std::string request;
    std::getline(std::cin, request);

    // wait for semaphore
    semop(sem_id, 0, -1);

    // write the request to the shared memory
    std::copy(request.begin(), request.end(), shared_memory);

    // release semaphore
    semop(sem_id, 0, 1);

    if (request.substr(0, 4) == "read") {
        sleep(1); // give server some time to process the read request
        semop(sem_id, 0, -1); // acquire semaphore again

        // read response from shared memory
        std::string response(shared_memory);

        // print the content of the file
        std::cout << "File content:\n" << response << std::endl;

        // clear shared memory
        std::fill_n(shared_memory, 1024, '\0');

        // release semaphore
        semop(sem_id, 0, 1);
    }

    // detach shared memory
    shmdt(shared_memory);

    return 0;
}

