    public class WeChatMessageHandler : MessageHandler<WeixinContext>
    {
        public WeChatMessageHandler(RequestMessageBase requestMessage)
            : base(requestMessage, 0)
        {
        }

        public override async Task<IResponseMessageBase> OnTextRequestAsync(RequestMessageText requestMessage)
        {
            var responseMessage = ResponseMessageBase.CreateFromRequestMessage<ResponseMessageText>(requestMessage);
            responseMessage.Content = $"欢迎关注微信公众号！您发送的消息是：{requestMessage.Content}";
            return await Task.FromResult(responseMessage);
        }

        public override async Task<IResponseMessageBase> OnEvent_SubscribeRequestAsync(RequestMessageEvent_Subscribe requestMessage)
        {
            var responseMessage = ResponseMessageBase.CreateFromRequestMessage<ResponseMessageText>(requestMessage);
            responseMessage.Content = "欢迎关注微信公众号！";
            return await Task.FromResult(responseMessage);
        }
    }
}
