#include <stdio.h>
#include <stdlib.h>
#include <semaphore.h>
#include <fcntl.h>
#include <unistd.h>

#define SEMAFOR_1 "/sem1"
#define SEMAFOR_2 "/sem2"

void handleSemaphore(sem_t* semaphore, int operation) {
    if (operation == 0) { // Acquire the semaphore
        if (sem_wait(semaphore) == -1) perror("sem_wait error");
        else printf("Semaphore acquired\n");
    } else { // Release the semaphore
        if (sem_post(semaphore) == -1) perror("sem_post error");
        else printf("Semaphore released\n");
    }
}

int main() {
    sem_t *sem1 = sem_open(SEMAFOR_1, O_CREAT, 0666, 1);
    sem_t *sem2 = sem_open(SEMAFOR_2, O_CREAT, 0666, 1);

    if (sem1 == SEM_FAILED || sem2 == SEM_FAILED) {
        perror("sem_open error");
        exit(EXIT_FAILURE);
    }

    char cmd;
    while (1) {
        printf("1 - acquire semaphore 1, q - release semaphore 1,\n2 - acquire semaphore 2, w - release semaphore 2\n");
        scanf(" %c", &cmd);
        switch (cmd) {
            case '1':
                handleSemaphore(sem1, 0);
                break;
            case 'q':
                handleSemaphore(sem1, 1);
                break;
            case '2':
                handleSemaphore(sem2, 0);
                break;
            case 'w':
                handleSemaphore(sem2, 1);
                break;
            default:
                printf("Unknown command\n");
        }
    }

    sem_close(sem1);
    sem_close(sem2);
    sem_unlink(SEMAFOR_1);
    sem_unlink(SEMAFOR_2);

    return 0;
}
