```java
import java.io.IOException;

public class CrossPlatformOpen {
    public static void openFile(String path) {
        String os = System.getProperty("os.name").toLowerCase();
        Runtime runtime = Runtime.getRuntime();

        try {
            if (os.contains("win")) {
                // On Windows, use 'explorer' to open the file or directory
                runtime.exec(new String[] {"explorer", path});
            } else if (os.contains("mac")) {
                // On macOS, use 'open' to open the file or directory
                runtime.exec(new String[] {"open", path});
            } else if (os.contains("nix") || os.contains("nux") || os.contains("aix")) {
                // On Unix/Linux, use 'xdg-open' to open the file or directory
                runtime.exec(new String[] {"xdg-open", path});
            } else {
                System.out.println("Unsupported operating system.");
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
