#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
using namespace std;

void Add(const vector<int>& in1, const vector<int>& in2, vector<int>& out) {

    int n = 0;
    for (int i = 0; i < max(in1.size(), in2.size()) || n; i++) {
        if (i == out.size()) {
            out.push_back(0);
        }

        int sum = n + (i < in1.size() ? in1[i] : 0) + (i < in2.size() ? in2[i] : 0);
        out[i] = sum % 26;
        n = sum / 26;
    }
}

void change(const string& let1, vector<int>& num1) {

    for (char j : let1) {
        if (isalpha(j)) {
            num1.push_back(j - 'A');
        }
    }
    reverse(num1.begin(), num1.end());
}

void Base26(const vector<int>& num1) {

    int k = num1.size() - 1;

    while (k > 0 && num1[k] == 0) {
        k--;
    }

    for (; k >= 0; k--) {
        cout << (char)(num1[k] + 'A');
    }
}

int main() {
    string expression;
    cout << "Input the expression: ";
    getline(cin, expression);

    vector<int> ans;
    vector<int> numnum;

    int time = 0;
    while (time < expression.size()) {
        int place = expression.find("+", time);
        if (place == string::npos) {
            place = expression.find("=", time);
        }
        change(expression.substr(time, place - time), numnum);
        Add(ans, numnum, ans);
        if (expression[place] == '=') {
            break;
        }
        time = place + 1;
        numnum.clear();
    }

    cout << "The answer is: ";
    Base26(ans);
    cout << endl;

    return 0;
}

