#include <iostream>
#include <array>
#include <algorithm>
#include <string>
using namespace std;

const int MAX_SIZE = 100;

void Add(const array<int, MAX_SIZE>& in1, const array<int, MAX_SIZE>& in2, array<int, MAX_SIZE>& out) {
    int n = 0;
    for (int i = 0; i < max(in1.size(), in2.size()) || n; i++) {
        if (i == out.size()) {
            out[i] = 0;
        }

        int sum = n + (i < in1.size() ? in1[i] : 0) + (i < in2.size() ? in2[i] : 0);
        out[i] = sum % 26;
        n = sum / 26;
    }
}

void change(const string& let1, array<int, MAX_SIZE>& num1) {
    for (char j : let1) {
        if (isalpha(j)) {
            num1.push_back(j - 'A');
        }
    }
    reverse(num1.begin(), num1.end());
}

void Base26(const array<int, MAX_SIZE>& num1) {
    int k = num1.size() - 1;

    while (k > 0 && num1[k] == 0) {
        k--;
    }

    for (; k >= 0; k--) {
        cout << (char)(num1[k] + 'A');
    }
    cout << endl;
}

int main() {
    string expression;
    cout << "Input the expression: ";
    getline(cin, expression);

    array<int, MAX_SIZE> ans;
    array<int, MAX_SIZE> numnum;

    int time = 0;
    while (time < expression.size()) {
        int place = expression.find("+", time);
        if (place == string::npos) {
            place = expression.find("=", time);
        }
        change(expression.substr(time, place - time), numnum);
        Add(ans, numnum, ans);
        if (expression[place] == '=') {
            break;
        }
        time = place + 1;
        numnum.fill(0);
    }

    cout << "The answer is: ";
    Base26(ans);

    return 0;
}
```

