  return Controller.extend("your.controller.name", {
    onInit: function() {
      var oVizFrame = this.byId("vizFrameId");
      oVizFrame.attachSelectData(this.onSelectData, this);
    },

    onSelectData: function(oEvent) {
      var aSelectedData = oEvent.getParameter("data");

      // Perform your custom logic when data is selected
      if (aSelectedData && aSelectedData.length > 0) {
        var oSelectedData = aSelectedData[0];
        var sCategory = oSelectedData.data.Name; // Example: Name is the data field
        var sValue = oSelectedData.data.Value;   // Example: Value is the data field

