  var oBinding = oList.getBinding("items");

  // Check if a sorter is already applied
  var bSortActive = oBinding.aSorters.length > 0;

  if (!bSortActive) {
    // No sorter applied, sort by "assign_modified_date" in descending order
    var oSorter = new sap.ui.model.Sorter("assign_modified_date", true);
    oBinding.sort(oSorter);

    // Store the sort order
    oList.data("sortOrder", "desc");
  } else {
    // Get the current sorter
    var oCurrentSorter = oBinding.aSorters[0];

    if (oCurrentSorter.sPath === "assign_modified_date") {
      // Check the sorting order
      if (oCurrentSorter.bDescending) {
        // Currently sorting in descending order, switch to ascending order
        oCurrentSorter.bDescending = false;
        oBinding.sort(oCurrentSorter);

        // Update the sort order
        oList.data("sortOrder", "asc");
      } else {
        // Currently sorting in ascending order, switch to descending order
        oCurrentSorter.bDescending = true;
        oBinding.sort(oCurrentSorter);

        // Update the sort order
        oList.data("sortOrder", "desc");
      }
    } else {
      // Sort by "assign_modified_date" in descending order by default
      var oNewSorter = new sap.ui.model.Sorter("assign_modified_date", true);
      oBinding.sort(oNewSorter);

      // Update the sort order
      oList.data("sortOrder", "desc");
    }
  }
},
