In the controller file:

```javascript
sap.ui.define([
   "sap/ui/core/mvc/Controller",
   "sap/m/Dialog",
   "sap/m/Table",
   "sap/m/Button",
   "sap/m/Text",
   "sap/m/Column",
   "sap/m/ColumnListItem"
], function (Controller, Dialog, Table, Button, Text, Column, ColumnListItem) {
   "use strict";

   return Controller.extend("yourController", {
      onInit: function () {
         this.oDialog = new Dialog({
            title: "Table Dialog",
            contentWidth: "50%",
            content: new Table({
               columns: [
                  new Column({ header: new Text({ text: "Column 1" }) })
               ],
               items: [
                  new ColumnListItem({ cells: [new Text({ text: "Item 1" })] })
               ]
            }),
            buttons: [
               new Button({
                  text: "Full Screen",
                  press: this.onFullScreenPress.bind(this)
               }),
               new Button({
                  text: "Close",
                  press: this.onCloseDialogPress.bind(this)
               })
            ]
         });

         this.getView().addDependent(this.oDialog);
      },

      onOpenDialogPress: function () {
         this.oDialog.open();
      },

      onFullScreenPress: function () {
         var oTable = this.oDialog.getContent()[0];
         oTable.toggleStyleClass("fullScreenTable");

         var bFullScreen = oTable.hasStyleClass("fullScreenTable");
         this.oDialog.setContentWidth(bFullScreen ? "100%" : "50%");
      },

      onCloseDialogPress: function () {
         this.oDialog.close();
      }
   });
});
