        // function to get the brightness of the pixel at (x, y)
        function getPixelBrightness(x, y) {
          const index = (x + y * img.width) * 4;
          const r = img.data[index];
          const g = img.data[index + 1];
          const b = img.data[index + 2];
          return (r * 299 + g * 587 + b * 114) / 1000;
        }

        // update the radius of the circle as time passes
        let startTime = null;
        const animation = (timestamp) => {
          if (!startTime) startTime = timestamp;
          const timeElapsed = (timestamp - startTime) / 1000;
          const brightness = getPixelBrightness(x, y);
          const speed = (255 - brightness) / 1000; // adjust speed based on brightness
          radius += speed * timeElapsed;
