	void timer2Init(void){
	RCC->APB1RSTR |=   RCC_APB1RSTR_TIM2RST;
	RCC->APB1RSTR &= ~(RCC_APB1RSTR_TIM2RST);//reset the TIM2 register
	RCC->APB1ENR  |=   RCC_APB1ENR_TIM2EN;//enable the TIM2 register
		// 1 kHz--1ms---------------------------------------
		//Ttimer=Tclock*prescalor*(autoreload+1)
	TIM2->PSC   = (40-1);		//prescaler
	TIM2->ARR   = (200-1);		//auto-reload
		//---------------------------------------------

	TIM2->DIER  = 0x0001;  // update interrupt enable 
	TIM2->CR1   = 0x0001;		//enable timer

	NVIC_EnableIRQ(TIM2_IRQn);
}
